    function parse_room_menu_list(list_alt,id_room_1) {
        for(var i=0;i<list_alt.length;i++) {
            var id = list_alt[i].id;
            var name = list_alt[i].name;
            var type = list_alt[i].type;
            var hide = parseInt(list_alt[i].hide);
            switch (type) {
                case 'room':
                    if(hide==0) {
                        if(id==id_room_1) {
                            var icon = 'fas fa-dot-circle';
                        } else {
                            var icon = 'far fa-circle';
                        }
                        $('.list_alt_menu .dropdown').append('<p data-ui-target="ui_edit_list_alt_controls" class="list_alt_'+id+'"><i class="'+icon+'"></i>&nbsp;&nbsp;&nbsp;'+name+'</p>');
                    }
                    break;
                case 'category':
                    $('.list_alt_menu .dropdown').append('<p data-ui-target="ui_edit_list_alt_controls" class="cat cat_'+id+'"><i class="fas fa-chevron-down"></i>&nbsp;&nbsp;&nbsp;'+name+'</p>');
                    var childrens = list_alt[i].childrens;
                    for(var k=0;k<childrens.length;k++) {
                        if(childrens[k]['hide']==0) {
                            if(childrens[k]['id']==id_room_1) {
                                var icon = 'fas fa-dot-circle';
                            } else {
                                var icon = 'far fa-circle';
                            }
                            $('.list_alt_menu .dropdown').append('<p data-ui-target="ui_edit_list_alt_controls" data-cat="'+id+'" class="children list_alt_'+childrens[k]['id']+' cat_parent_'+id+'"><i style="margin-left: 15px;" class="'+icon+'"></i>&nbsp;&nbsp;&nbsp;'+childrens[k]['name']+'</p>');
                        }
                    }
                    break;
            }
        }
    }

    function fix_colors_menu() {
        var first = null;
        var last = null;
        $('.menu_controls').find("p:not(.ui_disabled):not(.ui_hidden)").sort(function(a, b) {
            return parseInt(a.style.order) > parseInt(b.style.order) ? 1 : -1;
        }).each(function () {
            if(first==null) {
                first=$(this);
            }
            last=$(this);
        });
        if(first!=null) {
            var bg_first = first.css('background-color');
            $('.menu_controls .arrow').css('border-bottom','10px solid '+bg_first);
            $('#menu_controls_mt').css('background-color',bg_first);
        }
        if(last!=null) {
            var bg_last = last.css('background-color');
            $('#menu_controls_md').css('background-color',bg_last);
        }
    }
</script>

